import React, { Component } from 'react';
import './input-panel.css';


class InputPanel extends Component {

    render() {
        // const styly = {
        //     width: '470px',
        //     border: 'none',
        //     outline: 'none',
        //     opacity: 0.3
        // };
    
    return( 
        <form className = 'input-panel'>
            <input className = 'item-a' placeholder = 'Write what you want ?' />
            <button type = 'button' className = 'btn btn-outline-light' onClick = { () => this.props.addNewItem('Hello friend')}>ADD NEW</button>
        </form>
    )
}
};

export default InputPanel;
