import React, { Component } from 'react';
import './todo-list-item.css';


class TodoListItem extends Component {
    
    state = {
        important: false
    };

    // finishAction = () => {
    //    this.setState(({ done }) => {
    //        return {
    //            done: !done
    //        }
    //    });
    // };
     importantAction = () => {
         this.setState(({ important }) => {
             return{
                 important: !important
             }
         })
     };

    render () {
    
    const { label, onDelete, onDone, done } = this.props;
    const { important } = this.state;
    let classNames = 'todo-list-item';

    if (done) {
        classNames += ' done';
    };
    if (important) {
        classNames += ' important';
    };

    return(
        <span className = { classNames }>
                <button type ='button' className = 'btn btn-outline' onClick = { this.importantAction }>
                    <i className ='fa fa-check'></i>
                </button>

            <span className = 'todo-list-item-label' onClick = { onDone }> { label } </span>

                <button type ='button' className = 'btn btn-outline' onClick = { onDelete }>
                    <i className ='fa fa-close'></i>
                </button>
        </span>
    )
}
};

export default TodoListItem;
