import './item-filter.css';

class ItemFilter extends Component {

    buttons = [
        { name: 'all', label: 'All' },
        { name: 'active', label: 'Active' },
        { name: 'complete', label: 'Complete' }
    ];
    
    render(){

        const buttons = this.buttons.map(({ name, label }) => {
            return(
                <button key = { name } className = 'btn btn-outline-dark' onClick = {() => onFilterChange()}>{ label }</button>
            )
        })
       
    return(
        <div className ='item-filter'>
        
                <h3>{ done } items left</h3>
                   
                        <div className ='btn-toolbar'>
                           { buttons }          
                        </div>
    
                <button type = 'button' className = 'btn btn-outline-dark'>Clear completed</button>
            
        </div>
    )
}
};

export default ItemFilter;
