import React, { Component } from 'react';
import './input-panel.css';


class InputPanel extends Component {

    state = {
        label: ''
    };

    changeItem = (event) => {
        this.setState({
            label: event,
        })
    };

    render() {
        const styly = {
            width: '470px',
            border: 'none',
            outline: 'none',
            opacity: 0.3
        };
    
    return( 
        <form className = 'input-panel'>
            <input placeholder = 'Write what you want ?' onChange = { this.changeItem } style = { styly }/>
            <button type = 'button' className = 'btn btn-outline-light' onClick = { () => this.props.addNewItem('Hello friend')}>ADD NEW</button>
        </form>
    )
}
};

export default InputPanel;
