import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './index.css';

import Header from './components/header';
import InputPanel from './components/input-panel';
import TodoList from './components/todo-list';
import ItemFilter from './components/item-filter';

class App extends Component {

    maxId = 10;

    state = { 
        todoData: [
            {label: 'Work', important: false, id: 1},
            {label: 'Work example', important: false, id: 2},
            {label: 'Just example', important: false, id: 3},
    ]
};
    createItem(label) {
        return {
            label,

            important: false,
            id: this.maxId++
        }
    }

    addItem = (text) => {
        const newItem = {
            label: text,
            important: false,
            id: this.maxId++
        }
        this.setState(({ todoData }) => {
            const newArray = [...todoData, newItem];
            return {
                todoData: newArray
            }
        });
    }

    deleteItem = (id) => {
        this.setState(({ todoData }) => {
            const idx = todoData.findIndex((el) => el.id === id);
            const newArray = [...todoData.slice(0, idx),...todoData.slice(idx + 1)];
            return {
                todoData: newArray
            }
        });
    };

    render(){
    return(
        <div>
            <Header />
            <InputPanel addNewItem = { this.addItem }/>
            <TodoList todoArray = { this.state.todoData } onDelete = { this.deleteItem }/>
            <ItemFilter done = {1}/>
        </div>
    )
}
};

ReactDOM.render(<App />, document.getElementById('root'));
