import React, { Component } from 'react';
import './todo-list-items.css';


class TodoListItems extends Component {
    
    state = {
        done: false
    };

    finishAction = () => {
       this.setState({ done: true });
    };

    render () {
    
    const { label, important = false } = this.props;
    const { done } = this.state;
    let className = 'todo-list-items';

    if (done) {
        className += ' done'
    }

    const style = {
        color: important ? 'red': 'black',
        fontWeight: important ? 'bold': 'normal'
        };

    return(
        <span className ={ cn}>
                <button type ='button' className = 'btn'>
                    <i className ='fa fa-check-square'></i>
                </button>

            <span className = 'todo-list-items-label' style = {style} onClick = { this.finishAction }> { label } </span>

                <button type ='button' className = 'btn btn-dlt'>
                    <i className ='fa fa-times'></i>
                </button>
        </span>
    )
}
};

export default TodoListItems;
