import React, { Component } from 'react';
import './input-panel.css';


class InputPanel extends Component {

    state = {
        label: ''
    };

    changeItem = (event) => {
        this.setState({
            label: event.target.value
        });
    };

    onSubmit = (event) => {
        event.preventDefault();
        this.props.addNewItem(this.state.label);
    }

    render() {
        const styly = {
            width: '470px',
            border: 'none',
            outline: 'none',
            opacity: 0.3
        };
    
    return( 
        <form className = 'input-panel' onSubmit = { this.onSubmit }>
            <input ty placeholder = 'Write what you want ?' onChange = { this.changeItem } style = { styly }/>
            <button type = 'button' className = 'btn btn-outline-light'>ADD NEW</button>
        </form>
    )
}
};

export default InputPanel;
