import React, { Component } from 'react';
import './todo-list-item.css';


class TodoListItem extends Component {
    
    state = {
        done: false,
        important: false
    };

    finishAction = () => {
       this.setState(({ done }) => {
           return {
               done: !done
           }
       });
    };
     importantAction = () => {
         this.setState({
            important: true
         })
     };

    render () {
    
    const { label, onDelete } = this.props;
    const { done, important } = this.state;
    let classNames = 'todo-list-item';

    if (done) {
        classNames += ' done';
    };
    if (important) {
        classNames += ' important';
    };

    return(
        <div>
            <button type ='button' className = 'btn btn-outline' onClick = { this.importantAction }>
              <i className ='fa fa-check'></i>
            </button>
            
        </div>
